import React, { Component } from 'react'
import readXlsxFile from 'read-excel-file'
import { Payback, TranSport } from '../apis/datas'
import { Button, Card, Select, Col, Table, Icon } from 'antd'
import 'antd/dist/antd.css'
import CheckLogin from '../components/CheckLogin'
import { withCookies } from 'react-cookie'

const { Option } = Select
const columns = [{
  title: 'วันที่',
  dataIndex: 'dates',
  key: 'dates',
  width: 100
}, {
  title: 'เลขพัสดุ',
  dataIndex: 'number',
  key: 'number',
  width: 150
}, {
  title: 'ราคาสินค้า	',
  dataIndex: 'price',
  key: 'price',
  width: 100
}, {
  title: 'ชื่อลูกค้า',
  dataIndex: 'customer',
  key: 'customer',
  width: 150
}, {
  title: 'ที่อยู่จัดส่งพัสดุ',
  dataIndex: 'address',
  key: 'address',
  width: 250
}, {
  title: 'รหัสไปรษณี',
  dataIndex: 'post',
  key: 'post',
  width: 100
}, {
  title: 'เบอร์ติดต่อ',
  dataIndex: 'phone',
  key: 'phone',
  width: 150
}]
class CodPayBack extends Component {
  constructor() {
    super()
    this.state = {
      file: [],
      tranSportList: [],
      transportSelect: []
    }
  }

  componentDidMount() {
    new Promise((resolve, rejects) => {
      resolve(TranSport())
    }).then(res => {
      this.setState({
        tranSportList: res
      })
    })
  }

  importExcel = () => {
    const input = document.getElementById('file')
    let result = []
    readXlsxFile(input.files[0], {})
      .then(res => {
        res.forEach(arr => {
          let arr0 = new Date(arr[0]).toLocaleDateString(),
            arr1 = arr[1],
            arr2 = arr[2].toLocaleString('en-US', { minimumFractionDigits: 2 }),
            arr3 = arr[3],
            arr4 = arr[4],
            arr5 = arr[5],
            arr6 = arr[6]
          result.push({
            dates: arr0.toString(),
            number: arr1,
            price: arr2,
            customer: arr3,
            address: arr4,
            post: arr5,
            phone: arr6
          })
        })
        var fill = result.filter((result) => {
          return result.number !== "เลขพัสดุ"
        })
        console.log(fill)
        this.setState({
          file: fill
        })
      })
  }

  Upload = () => {
    const { cookies } = this.props
    let user = cookies.get('userNumber'),
      store = cookies.get('storeNumber'),
      excel = this.state.file,
      transport_comp = this.state.transportSelect.toString()
    if (excel.length > 0) {
      new Promise((resolve, rejects) => {
        var a = Payback(user, transport_comp, excel, store)
        resolve(a)
      }).then(res => {
        console.log(res)
      })
        .catch(err => { console.log(err) })
    }
  }

  TranSportSelect = (value) => {
    console.log(value)
    this.setState({
      transportSelect: value
    })
  }

  render() {
    return (
      <div style={{ padding: "2.7% 0 0 0" }}>
        <CheckLogin />
        <div style={{
          backgroundColor: "#f7f7f7",
          width: "100%",
          height: "100%",
          position: "absolute"
        }}>
          <br />
          <Col lg={{ span: 6, offset: 2 }} >
            <Card style={{ boxShadow: "0 3px 6px 0 rgba(0, 0, 0, 0.2)" }}>
              <div style={{ textAlign: "center" }}>อัพโหลด shoping payback</div>
              <br />
              <input type="file" id="file" onChange={this.importExcel} style={{ width: 180, }} />
              <br />
              <br />
              <label>
                <span>บริษัทขนส่ง : </span>
                <Select
                  id={"transport"}
                  style={{ width: 200 }}
                  onChange={this.TranSportSelect}
                >
                  {this.state.tranSportList.map((data, index) => (
                    <Option key={index} value={data.transport_company_number}>{data.transport_company_name}</Option>
                  ))}
                </Select>
              </label>
              <br />
              <br />
              <Button block onClick={this.Upload}>upload</Button>
            </Card>
          </Col>
          <Col lg={{ span: 13, offset: 1 }}>
            <Table style={{ boxShadow: "0 3px 6px 0 rgba(0, 0, 0, 0.2)" }} size="small" columns={columns} dataSource={this.state.file} scroll={{ x: 500, y: 500 }} />
          </Col>
        </div>
      </div >
    )
  }
}

export default withCookies(CodPayBack)
