import React, { Component } from 'react'
import { AllOrders, WaitingOrders, PayBackOrders, Excess, Absent } from '../apis/datas'
import 'antd/dist/antd.css'
import { Card, Table } from 'antd'

class databoard extends Component {

    constructor() {
        super()
        this.state = {
            user: '1234',

            AllOdersData: [],
            AllOdersRow: 0,
            AllOdersMonney: 0,

            WaitingData: [],
            WaitingRow: 0,
            WaitingMonney: 0,

            PayBackData: [],
            PayBackRow: 0,
            PayBackMonney: 0,

            ExcessData: [],
            ExcessRow: 0,
            ExcessMonney: 0,

            AbsentData: [],
            AbsentRow: 0,
            AbsentMonney: 0
        }
    }
    componentDidMount() {
        var user = this.state.user.toString()

        new Promise((resolve, reject) => {
            let orders = AllOrders(user)
            resolve(orders)
        })
            .then(res => {
                let AllOdersRow = res.length
                let totalMonney = null
                res.forEach(v => {
                    totalMonney = +totalMonney + +v.price
                    return totalMonney
                })
                this.setState({
                    AllOdersData: res,
                    AllOdersRow: AllOdersRow,
                    AllOdersMonney: totalMonney,
                })
            })

        new Promise((resolve, reject) => {
            let waitingOrder = WaitingOrders(user)
            resolve(waitingOrder)
        })
            .then(res => {
                let WaitingRow = res.length
                let totalMonney = null
                res.forEach(v => {
                    totalMonney = +totalMonney + +v.price
                })
                this.setState({
                    WaitingData: res,
                    WaitingRow: WaitingRow,
                    WaitingMonney: totalMonney,
                })
            })

        new Promise((resolve, reject) => {
            let paybackorder = PayBackOrders(user)
            resolve(paybackorder)
        })
            .then(res => {
                let PayBackRow = res.length
                let totalMonney = null
                res.forEach(v => {
                    totalMonney = +totalMonney + +v.price
                })
                this.setState({
                    PayBackData: res,
                    PayBackRow: PayBackRow,
                    PayBackMonney: totalMonney,
                })
            })

        new Promise((resolve, reject) => {
            let excess = Excess(user)
            resolve(excess)
        })
            .then(res => {
                let ExcessRow = res.length
                let totalMonney = null
                res.forEach(v => {
                    totalMonney = +totalMonney + +v.received_total
                    return totalMonney
                })
                this.setState({
                    ExcessData: res,
                    ExcessRow: ExcessRow,
                    ExcessMonney: totalMonney
                })
            })
        //
        new Promise((resolve, rejects) => {
            let absent = Absent(user)
            resolve(absent)
        })
            .then(res => {
                let AbsentRow = res.length
                let totalMonney = null
                res.forEach(v => {
                    totalMonney = +totalMonney + +v.received_total
                    return totalMonney
                })
                this.setState({
                    AbsentData: res,
                    AbsentRow: AbsentRow,
                    AbsentMonney: totalMonney
                })
            })
    }

    Cards=(e)=>{
        console.log(e)
    }
    render() {
        return (
            <div>
                <Card  onClick={this.Cards}>
                    <h3>{this.state.AllOdersMonney} บาท</h3>
                    <h4>รายการทั้งหมด</h4>
                    <h4>{this.state.AllOdersRow} ออเดอร์</h4>
                </Card>
                <Card>
                    <h3>{this.state.WaitingMonney} บาท</h3>
                    <h4>รายการรอตรวจสอบ</h4>
                    <h4>{this.state.WaitingRow} ออเดอร์</h4>
                </Card>
                <Card>
                    <h3>{this.state.PayBackMonney} บาท</h3>
                    <h4>ได้รับเงินครบถ้วนแล้ว</h4>
                    <h4>{this.state.PayBackRow} ออเดอร์</h4>
                </Card>
                <Card>
                    <h3>{this.state.ExcessMonney} บาท</h3>
                    <h4>ได้รับเงินเกิน</h4>
                    <h4>{this.state.ExcessRow} ออเดอร์</h4>
                </Card>
                <Card>
                    <h3>{this.state.AbsentMonney} บาท</h3>
                    <h4>ได้รับเงินคืนไม่ครบ</h4>
                    <h4>{this.state.AbsentRow} ออเดอร์</h4>
                </Card>
                <Table columns={columns} dataSource={this.state.AbsentData} />
            </div >
        )
    }
}


export default databoard
const columns = [{
    title: 'วันที่',
    dataIndex: 'dates',
    key: 'dates',
  }, {
    title: 'เลขพัสดุ',
    dataIndex: 'number',
    key: 'number',
  }, {
    title: 'ราคาสินค้า',
    dataIndex: 'price',
    key: 'price',
  }, {
    title: 'ชื่อลูกค้า',
    dataIndex: 'customer',
    key: 'customer',
  }, {
    title: 'ที่อยู่จัดส่งพัสดุ',
    dataIndex: 'address',
    key: 'address',
  }, {
    title: 'รหัสไปรษณี',
    dataIndex: 'post',
    key: 'post',
  }, {
    title: 'เบอร์ติดต่อ',
    dataIndex: 'phone',
    key: 'phone',
  },
  ]
