import React, { Component } from 'react'
import { Input, Button, Card, Form, InputNumber } from 'antd'
import 'antd/dist/antd.css'

class Comp_regit extends Component {
    constructor() {
        super()
        this.state = {
            emailHelp: '',
            emailStatus:'warning'
        }
    }
    Regit = (e) => {
        let email = document.getElementById('email'),
            password = document.getElementById('password'),
            confirm_password = document.getElementById('confirm'),
            store = document.getElementById('store'),
            phone = document.getElementById('phone')

        if (email.value.includes('@')===true ) {
            this.setState({
                emailHelp: "",
                emailStatus:'success'
            })
        }
        else if (email.value.includes('@')!==false){
            this.setState({
                emailHelp: "รูปแบบemailไม่ถูกต้อง",
                emailStatus:'error'
            })
        }
    }
    render() {
        return (
            <div>
                <Card>
                    <Form layout="vertical" >
                        <Form.Item label={"E-mail"} validateStatus={this.state.emailStatus} help={this.state.emailHelp}>
                            <Input type={"email"} id={'email'} />
                        </Form.Item>
                        <Form.Item label={"Password"}>
                            <Input.Password id={"password"} />
                        </Form.Item>
                        <Form.Item label={"Confirm Password"}>
                            <Input.Password id={"confirm"} />
                        </Form.Item>
                        <Form.Item label={"ชื่อร้านค้า"}>
                            <Input id={"store"} />
                        </Form.Item>
                        <Form.Item label={"เบอร์โทร"}>
                            <Input id={"phone"} />
                        </Form.Item>
                        <Form.Item>
                            <Button onClick={this.Regit}>Regiter</Button>
                        </Form.Item>
                    </Form>

                </Card>
            </div>
        )
    }
}
export default Comp_regit
